import Navbar from './Navbar';
import Home from './Home';
import{ BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Create from './Create';
import BlogDetails from './BlogDetails';

function App() {
  return (
    <Router>
    <div className="App">
      <Navbar />
      <div className="content">
        <Switch>
          <Route exact path="/">
            <Home/>
          </Route>
          <Route path="/create">
            <Create/> 
          </Route>
          <Route path="/blog/:id">
            <BlogDetails/>
          </Route>
        </Switch>
      </div>
    </div>
    </Router>
  );
}


// nest the Home component inside the Route component, and then nest the Route component inside the Switch component.
// The Switch component will only render the first route that matches the path.


// we used exact path for it otherwise creatr route will also be rendered to home 

// Switch component will only render the first route that matches the path.
// always surround the Route components with a Switch component.




export default App;
