import { useEffect, useState } from "react";
import BlogList from "./BlogList";
import useFetch from "./useFetch";



// USING A CUSTOM HOOK
// using USEFETCH to place the fetching process in
// a custom hook
// we will create a new file useFetch.js
// we will place the fetching process in it
// we will export it
// we will import it here
// we will use it here
// we will use it in the same way as we use useState


const Home = () => {
    const { error, isPending, data: blogs } = useFetch('http://localhost:8000/blogs')

    return (
      <div className="home">
        { error && <div>{ error }</div> }
        { isPending && <div>Loading...</div> }
        { blogs && <BlogList blogs={blogs} /> }
      </div>
    );
  }


// return mein ek conditional statment hai

export default Home;
