import { useState } from "react";
import { useEffect } from "react";
import BlogList from "./BlogList";

const Home = () => {
const [blogs,setBlogs]=useState(null)
const[isPending,setIsPending]=useState(true);
const [error,setError]=useState(null);


function handleDelete(id){
    const newBlogs=blogs.filter(blog=>blog.id!==id);
    setBlogs(newBlogs);
}




// using fetch data with useeffect

// once fetched we get a repsonse object we need to convert it to json



// make fetch 1 sec late
useEffect(()=>{
    setTimeout(()=>{
    fetch("http://localhost:8000/blogs")
    .then(res=>{
        if(!res.ok){
            throw Error("Could not fetch the data for that resource");
        }
        else{
        return res.json();
        }
    })
    .then(data=>{
        setBlogs(data);
        setIsPending(false);
    })
    .catch(err=>{
        setError(err.message);
        });
    },500)
},[]);


// if name chnages it will run the useeffect



// we will use fetch data to fetch from db.json
//  /blogs GET Fetche all blogs
// /blogs POST Add a new blog
// /blogs/:id GET Fetch a single blog
//blogs/:id DELETE Delete a single blog



return (
    <div className="home">
        {error && <div>{error}</div>}
        {isPending && <div>Loading...</div>}
        {blogs && <BlogList blogs={blogs} title="All Blogs!!!" handleDelete={handleDelete}/>}
    </div>
);
}

// return mein ek conditional statment hai

export default Home;
