import { useParams } from "react-router-dom";
import useFetch from "./useFetch";

// how to get the id from the url
// we can use useParams hook
// useParams hook is used to get the parameters from the url
// useParams hook is a part of the react-router-dom library.
// useParams hook returns an object with a property called id.
// The id property will be the value of the id parameter in the URL.
// The id property will be a string.



// using fetch to get the data from the server


const handleClick=()=>{
    fetch('http://localhost:8000/blogs'+blog.id),{
        method:'DELETE'
    }
}



const BlogDetails = () => {
    const {id} = useParams();
    const {data:blog, isPending, error} = useFetch('http://localhost:8000/blogs/' + id);
    return (  
        <div className="Blog-details">
            {isPending && <div>Loading...</div>}
            {error && <div>{error}</div>}
            {blog && (
                <article>
                    <h2>{blog.title}</h2>
                    <p>Written by {blog.author}</p>
                    <div>{blog.body}</div>
                    <button onClick={handleClick}>delete</button>
                </article>
            )}
        </div>
    );
}

export default BlogDetails;




